/****************************************************
 * PCS â€“ Normalize & Reconcile (versÃ£o 2 â€“ robusta)
 * - LÃª TODOS os itens de cada nÃ³ GET (por nome)
 * - Achata respostas: [], {data:[]}, {items:[]}, objeto
 * - Consolida por identificadorNavio
 * - Regras simples de alertas e statusResumo
 ****************************************************/

// === 1) Helpers de leitura ===
// LÃª TODOS os itens gerados por um nÃ³ e "achata" o que for array/objeto {data:[]} ou {items:[]}
function listFrom(nodeName) {
  try {
    const items = $items(nodeName) || [];
    const out = [];
    for (const it of items) {
      const j = it.json;
      if (!j) continue;

      if (Array.isArray(j)) {
        // resposta Ã© um array bruto: [ {...}, {...} ]
        out.push(...j);
      } else if (Array.isArray(j.data)) {
        // resposta veio embrulhada: { data: [ {...}, {...} ] }
        out.push(...j.data);
      } else if (Array.isArray(j.items)) {
        // variante: { items: [ {...} ] }
        out.push(...j.items);
      } else {
        // objeto simples jÃ¡ Ã© 1 registro
        out.push(j);
      }
    }
    return out;
  } catch (e) {
    // nÃ³ nÃ£o executou ou nÃ£o hÃ¡ itens
    return [];
  }
}

// === 2) Carrega listas das 4 fontes ===
const agList = listFrom("GET AgÃªncia");
const auList = listFrom("GET Autoridade");
const prList = listFrom("GET Praticagem");
const teList = listFrom("GET Terminal");

// (opcional) mÃ©tricas de depuraÃ§Ã£o
const counts = { agencia: agList.length, autoridade: auList.length, praticagem: prList.length, terminal: teList.length };

// === 3) Agrupa por identificadorNavio ===
const toMap = (list, key) => {
  const m = new Map();
  for (const row of list) {
    const id = row?.[key];
    if (!id) continue;
    if (!m.has(id)) m.set(id, []);
    m.get(id).push(row);
  }
  return m;
};

const mAg = toMap(agList, "identificadorNavio");
const mAu = toMap(auList, "identificadorNavio");
const mPr = toMap(prList, "identificadorNavio");
const mTe = toMap(teList, "identificadorNavio");

// === 4) Consolida por navio ===
const vesselIds = new Set([...mAg.keys(), ...mAu.keys(), ...mPr.keys(), ...mTe.keys()]);
const vessels = [];
const alerts = [];
const LIMIAR_MINUTOS = 15;

function diffMinutes(a, b) {
  if (!a || !b) return null;
  const A = new Date(a).getTime();
  const B = new Date(b).getTime();
  if (isNaN(A) || isNaN(B)) return null;
  return Math.round((B - A) / 60000);
}

for (const vessel_id of vesselIds) {
  const ag = (mAg.get(vessel_id) || [])[0] || null;
  const au = (mAu.get(vessel_id) || [])[0] || null;
  const pr = (mPr.get(vessel_id) || [])[0] || null;
  const te = (mTe.get(vessel_id) || [])[0] || null;

  // 4.1 objeto canÃ´nico mÃ­nimo
  const can = {
    vessel_id,
    agency: ag ? {
      id: ag.id,
      nomeAgencia: ag.nomeAgencia,
      manifestoEntregue: ag.manifestoEntregue ?? null,
      statusDocumentacao: ag.statusDocumentacao ?? null,
      dataEnvioInformacoes: ag.dataEnvioInformacoes ?? null,
      documentosAdicionais: ag.documentosAdicionais ?? null
    } : null,
    authority: au ? {
      id: au.id,
      tipoMovimentacao: au.tipoMovimentacao ?? null,
      dataSolicitacao: au.dataSolicitacaoAcesso ?? null,
      dataAutorizacao: au.dataAutorizacaoAcesso ?? null,
      status: au.statusAutorizacao ?? null,
      motivo: au.motivoIntercorrencia ?? null,
      observacoes: au.observacoes ?? null
    } : null,
    pilotage: pr ? {
      id: pr.id,
      tipo: pr.manobraTipo ?? null,
      dataSolicitacao: pr.dataSolicitacao ?? null,
      dataExecucao: pr.dataExecucao ?? null,
      status: pr.status ?? null,
      motivo: pr.motivoIntercorrencia ?? null,
      observacoes: pr.observacoes ?? null
    } : null,
    terminal: te ? {
      id: te.id,
      terminal: te.nomeTerminal ?? null,
      dataPrevistaAtracacao: te.dataPrevistaAtracacao ?? null,
      dataRealAtracacao: te.dataRealAtracacao ?? null,
      tipoOperacao: te.tipoOperacao ?? null,
      statusOperacao: te.statusOperacao ?? null,
      observacoes: te.observacoes ?? null
    } : null
  };

  // 4.2 regras de ALERTA
  // (1) Bloqueio documental â€“ AgÃªncia
  if (can.agency?.statusDocumentacao && can.agency.statusDocumentacao !== "completo") {
    alerts.push({
      type: "BloqueioDocumental",
      vessel_id,
      statusDocumentacao: can.agency.statusDocumentacao,
      suggestion: "Regularizar documentaÃ§Ã£o (manifesto/fiscal) antes da manobra/operaÃ§Ã£o."
    });
  }

  // (2) Acesso pendente/negado â€“ Autoridade
  if (can.authority?.status === "pendente") {
    alerts.push({
      type: "AcessoPendente",
      vessel_id,
      reason: can.authority.motivo || "Em anÃ¡lise",
      suggestion: "Reprogramar janela com Autoridade e Praticagem."
    });
  }
  if (can.authority?.status === "negado") {
    alerts.push({
      type: "AcessoNegado",
      vessel_id,
      reason: can.authority.motivo || "Sem motivo informado",
      suggestion: "Resolver intercorrÃªncia com armador/Autoridade."
    });
  }

  // (3) Conflito de horÃ¡rios â€“ AutorizaÃ§Ã£o (Autoridade) vs ExecuÃ§Ã£o (Praticagem)
  const dAut = can.authority?.dataAutorizacao || null;
  const dExec = can.pilotage?.dataExecucao || null;
  const delta = diffMinutes(dAut, dExec);
  if (delta !== null && Math.abs(delta) > LIMIAR_MINUTOS) {
    alerts.push({
      type: "DataMismatch",
      vessel_id,
      fields: { dataAutorizacao: dAut, dataExecucaoManobra: dExec, deltaMin: delta },
      suggestion: "Alinhar janela SPA Ã— Praticagem; sinalizar ao Terminal."
    });
  }

  // (4) Terminal aguardando (sem atracaÃ§Ã£o real)
  const statusOp = can.terminal?.statusOperacao || "";
  const realAtrac = can.terminal?.dataRealAtracacao || null;
  if (can.terminal && !realAtrac && typeof statusOp === "string" && statusOp.startsWith("aguardando")) {
    alerts.push({
      type: "TerminalAguardando",
      vessel_id,
      statusOperacao: statusOp,
      suggestion: "Checar bloqueios (documentos/janela/manobra) e replanejar."
    });
  }

  // 4.3 status-resumo do navio (para painel/Slack)
  let statusResumo = "ok";
  const has = (t) => alerts.find(a => a.vessel_id === vessel_id && a.type === t);
  if (has("AcessoNegado")) statusResumo = "bloqueado";
  else if (has("BloqueioDocumental")) statusResumo = "bloqueado_documento";
  else if (has("AcessoPendente")) statusResumo = "pendente_autorizacao";
  else if (has("DataMismatch")) statusResumo = "conflito_horarios";
  else if (has("TerminalAguardando")) statusResumo = "aguardando_navio";

  vessels.push({ ...can, statusResumo });
}

// === 5) Retorno Ãºnico ===
return [{
  json: {
    vessels,
    alerts,
    counts, // Ãºtil para validar que estÃ¡ lendo os 4 nÃ³s
    generatedAt: new Date().toISOString()
  }
}];

